{
  "type": "record",
  "value": {
    "description": "A fantastical story world with super-powered beings.",
    "fields": [
      {
        "description": "A collection of beings in the world.",
        "key": "beings",
        "value": {
          "type": "array",
          "value": {
            "description": "A super-powered being in the world.",
            "items": {
              "type": "record",
              "value": {
                "description": "Details of a being.",
                "fields": [
                  {
                    "description": "The name of the being.",
                    "key": "name",
                    "value": {
                      "type": "atom",
                      "value": {
                        "description": "The name of the being.",
                        "atomType": "string"
                      }
                    }
                  },
                  {
                    "description": "The type of being (e.g., witch, vampire, werewolf, fae).",
                    "key": "type",
                    "value": {
                      "type": "enum",
                      "value": {
                        "description": "Type of the being.",
                        "variants": [
                          "witch",
                          "vampire",
                          "werewolf",
                          "fae"
                        ]
                      }
                    }
                  },
                  {
                    "description": "The powers possessed by the being.",
                    "key": "powers",
                    "value": {
                      "type": "array",
                      "value": {
                        "description": "A power that the being possesses.",
                        "items": {
                          "type": "atom",
                          "value": {
                            "description": "The name of the power.",
                            "atomType": "string"
                          }
                        }
                      }
                    }
                  },
                  {
                    "description": "The origin story of the being.",
                    "key": "origin",
                    "value": {
                      "type": "atom",
                      "value": {
                        "description": "A brief origin story of the being.",
                        "atomType": "string"
                      }
                    }
                  },
                  {
                    "description": "The alliances or factions the being belongs to.",
                    "key": "factions",
                    "value": {
                      "type": "array",
                      "value": {
                        "description": "A faction that the being is part of.",
                        "items": {
                          "type": "atom",
                          "value": {
                            "description": "The name of the faction.",
                            "atomType": "string"
                          }
                        }
                      }
                    }
                  }
                ]
              }
            }
          }
        }
      }
    ]
  }
}
