{
  "type": "record",
  "value": {
    "description": "A fantastical story world with super-powered beings.",
    "fields": [
      {
        "description": "A record representing a super-powered being.",
        "key": "being",
        "value": {
          "type": "record",
          "value": {
            "description": "Details of a super-powered being.",
            "fields": [
              {
                "description": "The name of the being.",
                "key": "name",
                "value": {
                  "type": "atom",
                  "value": {
                    "description": "The name of the being.",
                    "atomType": "string"
                  }
                }
              },
              {
                "description": "The type of being (e.g., witch, vampire, werewolf, fae).",
                "key": "type",
                "value": {
                  "type": "enum",
                  "value": {
                    "description": "Type of the being.",
                    "variants": [
                      "witch",
                      "vampire",
                      "werewolf",
                      "fae"
                    ]
                  }
                }
              },
              {
                "description": "The powers possessed by the being.",
                "key": "powers",
                "value": {
                  "type": "array",
                  "value": {
                    "description": "List of powers.",
                    "items": {
                      "type": "atom",
                      "value": {
                        "description": "A power of the being.",
                        "atomType": "string"
                      }
                    }
                  }
                }
              },
              {
                "description": "The origin story of the being.",
                "key": "origin",
                "value": {
                  "type": "atom",
                  "value": {
                    "description": "The origin story of the being.",
                    "atomType": "string"
                  }
                }
              },
              {
                "description": "The allies of the being.",
                "key": "allies",
                "value": {
                  "type": "array",
                  "value": {
                    "description": "List of allies.",
                    "items": {
                      "type": "record",
                      "value": {
                        "description": "An ally of the being.",
                        "fields": [
                          {
                            "description": "The name of the ally.",
                            "key": "name",
                            "value": {
                              "type": "atom",
                              "value": {
                                "description": "The name of the ally.",
                                "atomType": "string"
                              }
                            }
                          },
                          {
                            "description": "The type of ally (e.g., human, another super-powered being).",
                            "key": "type",
                            "value": {
                              "type": "enum",
                              "value": {
                                "description": "Type of the ally.",
                                "variants": [
                                  "human",
                                  "witch",
                                  "vampire",
                                  "werewolf",
                                  "fae"
                                ]
                              }
                            }
                          }
                        ]
                      }
                    }
                  }
                }
              }
            ]
          }
        }
      }
    ]
  }
}
