{
  "type": "record",
  "value": {
    "description": "A fantastical story world with super-powered beings.",
    "fields": [
      {
        "description": "A collection of beings in the world.",
        "key": "beings",
        "value": {
          "type": "array",
          "value": {
            "description": "A super-powered being in the world.",
            "items": {
              "type": "record",
              "value": {
                "description": "Details of a being.",
                "fields": [
                  {
                    "description": "The name of the being.",
                    "key": "name",
                    "value": {
                      "type": "atom",
                      "value": {
                        "description": "The name of the being.",
                        "atomType": "string"
                      }
                    }
                  },
                  {
                    "description": "The type of being (e.g., witch, vampire, werewolf, fae).",
                    "key": "type",
                    "value": {
                      "type": "enum",
                      "value": {
                        "description": "Type of being.",
                        "variants": [
                          "witch",
                          "vampire",
                          "werewolf",
                          "fae"
                        ]
                      }
                    }
                  },
                  {
                    "description": "The powers possessed by the being.",
                    "key": "powers",
                    "value": {
                      "type": "array",
                      "value": {
                        "description": "A power that the being possesses.",
                        "items": {
                          "type": "record",
                          "value": {
                            "description": "Details of a power.",
                            "fields": [
                              {
                                "description": "The name of the power.",
                                "key": "name",
                                "value": {
                                  "type": "atom",
                                  "value": {
                                    "description": "The name of the power.",
                                    "atomType": "string"
                                  }
                                }
                              },
                              {
                                "description": "The description of the power.",
                                "key": "description",
                                "value": {
                                  "type": "atom",
                                  "value": {
                                    "description": "Description of the power.",
                                    "atomType": "string"
                                  }
                                }
                              },
                              {
                                "description": "The level of mastery of the power (e.g., novice, expert, master).",
                                "key": "mastery",
                                "value": {
                                  "type": "enum",
                                  "value": {
                                    "description": "Mastery level of the power.",
                                    "variants": [
                                      "novice",
                                      "expert",
                                      "master"
                                    ]
                                  }
                                }
                              }
                            ]
                          }
                        }
                      }
                    }
                  }
                ]
              }
            }
          }
        }
      }
    ]
  }
}
