{
  "type": "record",
  "value": {
    "description": "A fantastical story world with super-powered beings.",
    "fields": [
      {
        "description": "A collection of beings in the world.",
        "key": "beings",
        "value": {
          "type": "array",
          "value": {
            "description": "A super-powered being.",
            "items": {
              "type": "record",
              "value": {
                "description": "Details of a being.",
                "fields": [
                  {
                    "description": "The name of the being.",
                    "key": "name",
                    "value": {
                      "type": "atom",
                      "value": {
                        "description": "The name of the being.",
                        "atomType": "string"
                      }
                    }
                  },
                  {
                    "description": "The type of being (e.g., witch, vampire, werewolf, fae).",
                    "key": "type",
                    "value": {
                      "type": "enum",
                      "value": {
                        "description": "Type of being.",
                        "variants": [
                          "witch",
                          "vampire",
                          "werewolf",
                          "fae"
                        ]
                      }
                    }
                  },
                  {
                    "description": "The powers possessed by the being.",
                    "key": "powers",
                    "value": {
                      "type": "array",
                      "value": {
                        "description": "A power that a being can possess.",
                        "items": {
                          "type": "atom",
                          "value": {
                            "description": "A power description.",
                            "atomType": "string"
                          }
                        }
                      }
                    }
                  },
                  {
                    "description": "The origin story of the being.",
                    "key": "origin",
                    "value": {
                      "type": "atom",
                      "value": {
                        "description": "Origin story of the being.",
                        "atomType": "string"
                      }
                    }
                  },
                  {
                    "description": "The allies of the being.",
                    "key": "allies",
                    "value": {
                      "type": "array",
                      "value": {
                        "description": "Allies of the being.",
                        "items": {
                          "type": "record",
                          "value": {
                            "description": "Details of an ally being.",
                            "fields": [
                              {
                                "description": "The name of the ally.",
                                "key": "name",
                                "value": {
                                  "type": "atom",
                                  "value": {
                                    "description": "The name of the ally.",
                                    "atomType": "string"
                                  }
                                }
                              },
                              {
                                "description": "The type of ally (e.g., witch, vampire, etc.).",
                                "key": "type",
                                "value": {
                                  "type": "enum",
                                  "value": {
                                    "description": "Type of ally.",
                                    "variants": [
                                      "witch",
                                      "vampire",
                                      "werewolf",
                                      "fae"
                                    ]
                                  }
                                }
                              }
                            ]
                          }
                        }
                      }
                    }
                  }
                ]
              }
            }
          }
        }
      }
    ]
  }
}
