message.content:
{
  "type": "record",
  "value": {
    "description": "A fantastical story world with super-powered beings.",
    "fields": [
      {
        "description": "A character in the story world.",
        "key": "characters",
        "value": {
          "type": "array",
          "value": {
            "description": "List of characters in the story.",
            "items": {
              "type": "record",
              "value": {
                "description": "A character with superpowers.",
                "fields": [
                  {
                    "description": "The name of the character.",
                    "key": "name",
                    "value": {
                      "type": "atom",
                      "value": {
                        "description": "The name of the character.",
                        "atomType": "string"
                      }
                    }
                  },
                  {
                    "description": "The type of superpower the character possesses.",
                    "key": "superpower",
                    "value": {
                      "type": "enum",
                      "value": {
                        "description": "Type of superpower.",
                        "variants": [
                          "witch",
                          "vampire",
                          "werewolf",
                          "fae"
                        ]
                      }
                    }
                  },
                  {
                    "description": "The character's abilities based on their superpower.",
                    "key": "abilities",
                    "value": {
                      "type": "array",
                      "value": {
                        "description": "List of abilities.",
                        "items": {
                          "type": "atom",
                          "value": {
                            "description": "An ability of the character.",
                            "atomType": "string"
                          }
                        }
                      }
                    }
                  },
                  {
                    "description": "The character's backstory.",
                    "key": "backstory",
                    "value": {
                      "type": "atom",
                      "value": {
                        "description": "A brief backstory of the character.",
                        "atomType": "string"
                      }
                    }
                  },
                  {
                    "description": "The character's allies in the story.",
                    "key": "allies",
                    "value": {
                      "type": "array",
                      "value": {
                        "description": "List of allies.",
                        "items": {
                          "type": "atom",
                          "value": {
                            "description": "An ally of the character.",
                            "atomType": "string"
                          }
                        }
                      }
                    }
                  },
                  {
                    "description": "The character's enemies in the story.",
                    "key": "enemies",
                    "value": {
                      "type": "array",
                      "value": {
                        "description": "List of enemies.",
                        "items": {
                          "type": "atom",
                          "value": {
                            "description": "An enemy of the character.",
                            "atomType": "string"
                          }
                        }
                      }
                    }
                  }
                ]
              }
            }
          }
        }
      }
    ]
  }
}
